// 389. Find the Difference
// You are given two strings s and t.
// String t is generated by random shuffling string s and then add one more letter at a random position.
// Return the letter that was added to t.


// Solution 1: Count Frequencies

// Time Complexity: O(n) 60ms
// Space Complexity: O(26) = O(1) 43.3MB
var findTheDifference = function(s, t) {
  let sFreq = Array(26).fill(0), tFreq = Array(26).fill(0);
  for (let char of s) sFreq[char.charCodeAt() - 97]++;
  for (let char of t) {
    let charCode = char.charCodeAt() - 97;
    if (sFreq[charCode] === tFreq[charCode]) return char;
    tFreq[charCode]++;
  }
};

// Solution 2: XOR

// XOR all the character codes from s and t.
// The end result will be the character from the xor.

// Time Complexity: O(n) 77ms
// Space Complexity: O(1) 42.8MB
var findTheDifference = function(s, t) {
  let xor = 0;
  for (let i = 0; i < s.length; i++) {
    xor ^= s.charCodeAt(i) - 97;
    xor ^= t.charCodeAt(i) - 97;
  }
  xor ^= t.charCodeAt(t.length - 1) - 97;
  return String.fromCharCode(xor + 97);
};

// Two test cases to run function on
console.log(findTheDifference("abcd", "abcde")) // "e"
console.log(findTheDifference("", "y")) // "y"